sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
   var requester = g_form.getValue('requester');
	var reviewer = g_form.getValue('reviewer');
    var approver = g_form.getValue('approver');

	if ( requester == reviewer ) {
        alert(getMessage('Cannot set Reviewer the same as Requester'));
        return false;
    }
    if (requester == approver ) {
        alert(getMessage('Cannot set Approver the same as Requester'));
        return false;
    }
}
