sys_script_include.script:
var ESARUtilsSNC = Class.create();
ESARUtilsSNC.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	PROP_COPY_ENABLED: 'EnableCopyRequest',

	initialize: function(request, responseXML, gc) {
		global.AbstractAjaxProcessor.prototype.initialize.call(this, request, responseXML, gc);
// 		this.arrayUtil = new ArrayUtil();
// 		this.log = new GSLog('com.snc.change_request.copy.log', 'ChangeUtilsSNC');

// 		this.STAY_ON_CURRENT_CHANGE_REQUEST_MAP[this.VERB_BACK] = true;
	},
	isCopyFlagValid: function() {
		//Read property definition
		var isCopyEnabledProp = j2js(gs.getProperty(this.PROP_COPY_ENABLED));

		if (isCopyEnabledProp === 'false') {
			return false;
		} else {
			return true;
		}

	},

	isCopyRulesValid: function(/*GlideRecord*/ changeRequest) {
		//Read service now rules for enabling copy
		var rulesValid = true;

		if (j2js(changeRequest.type) === 'standard') {
			rulesValid = false;
		}

		return rulesValid;
	},

    type: 'ESARUtilsSNC'
});
