sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    debugger;
    var comment = api.state.approvalComments;
    // if(comment){
        var action = event.elementId;
        api.data.update_record_1.execute({'table': api.context.props.table,'recordId': api.context.props.sysId, 'templateFields': 'comments='+comment+'^work_notes='+comment+'^status='+action, 'useSetDisplayValue': '' });
        if (action == 'returned') {
            helpers.modal.close("return_modal");
        } else if (action == 'rejected') {
            helpers.modal.close("reject_modal");
        } else if (action == 'approved') {
            helpers.modal.close("approve_modal");
        } else if (action == 'confirmed') {
            helpers.modal.close("confirm_model");
        }
    // }
}
